class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& hor, vector<int>& ver) {
        int row=1,col=1;
        sort(hor.rbegin(),hor.rend());
        sort(ver.rbegin(),ver.rend());
        int i=0,j=0;

        long long cost = 0;
        while(i<n-1&&j<m-1){
            if(hor[i]>ver[j]){
                cost+=row*hor[i];
                col++;
                i++;
            }
            else {
                cost+=col*ver[j];
                row++;
                j++;
            }
        }
        for(;j<m-1;j++){
            cost+=col*ver[j];
        }
        for(;i<n-1;i++){
            cost+=row*hor[i];
        }
        return cost;
    }
};
