class Solution {
public:
    long long  minimumCostOfBreaking(vector<int>& X, vector<int>& Y, int m, int n) {
        long long  res = 0;
        sort(X.begin(), X.end(), greater<int>());
        sort(Y.begin(), Y.end(), greater<int>());
        int h = 1, v = 1;
        int i = 0, j = 0;
        while (i < m && j < n) {
            if (X[i] > Y[j]) {
                res += X[i] * v;
                h++;
                i++;
            } else {
                res += Y[j] * h;
                v++;
                j++;
            }
        }
        while (i < m) {
            res += X[i] * v;
            i++;
        }
        while (j < n) {
            res += Y[j] * h;
            j++;
        }

        return res;
    }
long long minimumCost(int m, int n, vector<int>& x, vector<int>& y) {
        return minimumCostOfBreaking(x, y, m-1, n-1);
    }
};

