function minimumCost(m: number, n: number, horizontalCut: number[], verticalCut: number[]): number {
  const horizontalArr = horizontalCut.sort((a, b) => a - b);
  const verticalArr = verticalCut.sort((a, b) => a - b);
  let row = 1;
  let col = 1;
  let ans = 0;
  while (horizontalArr.length || verticalArr.length) {
    let next: number = 0;
    if (!horizontalArr.length || horizontalArr[horizontalArr.length - 1] < verticalArr[verticalArr.length - 1]) {
      next = (verticalArr.pop() as number) * row;
      col++;
    } else {
      next = (horizontalArr.pop() as number )* col;
      row++;
    }
    ans += next;
  }
  return ans;
};
