class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
            hCuts = 0
            vCuts = 0

            costs = []

            for cost in horizontalCut:
                costs.append((cost, True))

            for cost in verticalCut:
                costs.append((cost, False))

            costs.sort(reverse=True)

            total = 0

            for cost, isHoriz in costs:
                times = 0
                if isHoriz:
                    hCuts += 1
                    times = vCuts + 1
                else:
                    vCuts += 1
                    times = hCuts + 1
                total += cost * times

            return total
