class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
           sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());

        long long hSegments = 1, vSegments = 1;
        long long cost = 0;
        long long hIdx = 0, vIdx = 0;

        while (hIdx < m - 1 && vIdx < n - 1) {
            if (h[hIdx] > v[vIdx]) {
                cost += h[hIdx] * vSegments;
                hIdx++;
                hSegments++;
            } else {
                cost += v[vIdx] * hSegments;
                vIdx++;
                vSegments++;
            }
        }

        while (hIdx < m - 1) {
            cost += h[hIdx] * vSegments;
            hIdx++;
        }

        while (vIdx < n - 1) {
            cost += v[vIdx] * hSegments;
            vIdx++;
        }

        return cost;
    }
};
