class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        total_cost = 0
        horizontalCut = sorted(horizontalCut, reverse=True)
        verticalCut = sorted(verticalCut, reverse=True)

        index = 0
        for h in horizontalCut:
            while index < len(verticalCut) and verticalCut[index] > h:
                index += 1
            total_cost += h * (index + 1)
            # print(h, index)

        index = 0
        for v in verticalCut:
            while index < len(horizontalCut) and horizontalCut[index] >= v:
                index += 1
            total_cost += v * (index + 1)
            # print(v, index)
        return total_cost
