class Solution
{
public:
    long long minimumCost(int m, int n, vector<int> &hc, vector<int> &vc)
    {
        long long cost = 0;
        sort(hc.begin(), hc.end());
        sort(vc.begin(), vc.end());
        int h = 1;
        int v = 1;
        while (hc.size() && vc.size())
        {
            int he = hc[hc.size() - 1], ve = vc[vc.size() - 1];
            if (he > ve)
            {
                cost += (he * v);
                h++;
                hc.erase(hc.end());
            }
            else if (he < ve)
            {
                cost += (ve * h);
                v++;
                vc.erase(vc.end());
            }
            else
            {
                if (h > v)
                {
                    cost += (he * v);
                    h++;
                    hc.erase(hc.end());
                }
                else
                {
                    cost += (ve * h);
                    v++;
                    vc.erase(vc.end());
                }
            }
        }
        while (hc.size())
        {
            cost += (hc[hc.size() - 1] * v);
            h++;
            hc.erase(hc.end());
        }
        while (vc.size())
        {
            cost += (vc[vc.size() - 1] * h);
            v++;
            vc.erase(vc.end());
        }
        return cost;
    }
};
