public class Solution {
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        List<long[]> list = new List<long[]>();
        foreach(int v in horizontalCut) list.Add(new long[]{0, v});
        foreach(int v in verticalCut) list.Add(new long[]{1, v});
        list.Sort((A, B) =>(Math.Sign(B[1] - A[1])));
        long ans = 0;
        long H = 1, V = 1;
        foreach(long[] v in list)
        {
            if(v[0] == 0)
            {
                ans += V * v[1];
                H++;
            }
            else
            {
                ans += H * v[1];
                V++;
            }
        }
        return ans;
    }
}
