class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        tag = [1,1]
        heap = []
        ans = 0
        heapq.heapify(heap) #堆化
        for x in horizontalCut:
            heapq.heappush(heap, (-x,1)) # 竖
        for x in verticalCut:
            heapq.heappush(heap, (-x,0)) # 横
        while heap:
            x,c = heapq.heappop(heap)
            ans -= x * tag[c]
            tag[c^1] += 1
            # print(x,c,ans,tag)
        return ans
