class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hC, vector<int>& vC) {
        int a=1;
        int b=1;
        sort(begin(hC),end(hC),greater<int>());
        sort(begin(vC),end(vC),greater<int>());
        long long ans=0;
        int mod=1e9+7;
        while(a!=m||b!=n){
            if(b==n||(a!=m&&hC[a-1]>vC[b-1])){
                ans+=(hC[a-1]*b)%mod;
                a++;
            }else{
                ans+=(vC[b-1]*a)%mod;
                b++;
            }
        }
        return ans;
    }
};
