class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        std::vector<std::pair<long long, int>> cnt;
        for (int i: h) {
            cnt.push_back(std::make_pair(i, 0));
        }
        for (int i: v) {
            cnt.push_back(std::make_pair(i, 1));
        }
        std::sort(cnt.rbegin(), cnt.rend());
        long long x = 1, y = 1, ans = 0;
        for (auto cur: cnt) {
            if (cur.second == 0) {
                ans += cur.first * y;
                ++x;
            } else {
                ans += cur.first * x;
                ++y;
            }
        }
        return ans;
    }
};
