using ii = pair<int,int>;
using lint = long long;
#define eb emplace_back
#define all(x) (x).begin(),(x).end()
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& ra, vector<int>& ca) {
        vector<ii> a;
        for(auto &x: ra) {
            a.eb(x, 0);
        }
        for(auto &x: ca) {
            a.eb(x, 1);
        }
        sort(all(a), [](auto u, auto v) {
            return u.first > v.first;
        });

        lint vc = 0, hc = 0;

        lint ret = 0;
        for(auto &[v, isH]: a) {
            if(isH) ret += v*(vc+1), hc++;
            else ret += v*(hc+1), vc++;
        }

        return ret;
    }
};
