using i64 = int64_t;

class Solution {
 public:
  long long minimumCost(int m, int n, vector<int>& horizontalCut,
                        vector<int>& verticalCut) {
    vector<pair<int, int>> p;
    for (int i : horizontalCut) p.emplace_back(i, 0);
    for (int i : verticalCut) p.emplace_back(i, 1);
    ranges::sort(p, greater());
    vector<int> c(2, 1);
    i64 res = 0;
    for (auto [x, y] : p) {
      res += i64(x) * c[y];
      c[y ^ 1] += 1;
    }
    return res;
  }
};
