class Solution {
    public long minimumCost(int n, int m, int[] h, int[] v) {
        long a[][] = new long[n + m][2];
        for(int i = 0; i < n - 1; i++)
            a[i] = new long[]{h[i], 0};
        for(int i = 0; i < m - 1; i++)
            a[i + n - 1] = new long[]{v[i], 1};
        Arrays.sort(a,
            new Comparator<long[]>(){
                public int compare(long x[], long y[]){
                    return (int)(y[0] - x[0]);
                }
            }
        );

        long ans = 0;
        long hor = 1, ver = 1;
        for(long x[] : a){
            if(x[1] == 0){
                ans += x[0] * hor;
                ver++;
            } else{
                ans += x[0] * ver;
                hor++;
            }
        }
        return ans;
    }
}
