class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());
        int hi = 0, vi = 0;
        long long ans = 0;
        for(int i = 0; i < (h.size()+v.size()); ++i){
            // cout << hi << " " << vi << endl;
            if(hi == h.size() || (vi < v.size() && v[vi] >= h[hi])){
                ans += v[vi]*(hi+1);
                ++vi;
            } else if (vi == v.size() || (hi < h.size() && h[hi] > v[vi])){
                ans += h[hi]*(vi+1);
                ++hi;
            }
        }
        return ans;
    }
};
