class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        arr = []
        for v in horizontalCut:
            heappush(arr, (-v,'h'))
        for v in verticalCut:
            heappush(arr, (-v,'w'))

        h,w = 1,1
        res = 0
        while len(arr) > 0:
            v,c = heappop(arr)
            #print(v,c,h,w)
            if c == 'h':
                res -= v*w
                h += 1
            else:
                res -= v*h
                w += 1
        return res
