class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        /*
        4 4 4 4 4 4 5
        4 4 4 5 5 5 5
        */
        long long ans = 0;
        vector<int>a;
        vector<int>b;
        int ca = 1;
        int cb = 1;
        for(int nxt: horizontalCut){
            a.push_back(nxt);
        }
        for(int nxt: verticalCut){
            b.push_back(nxt);
        }
        sort(a.begin(),a.end());
        sort(b.begin(),b.end());
        while(a.size() || b.size()){
            if(!b.size()){
               ans+=1ll*a.back()*cb;
                a.pop_back();
                ca++;
            }
            else if(!a.size()){
                ans+=1ll*b.back()*ca;
                b.pop_back();
                cb++;
            }
            else if(a.back()> b.back()){
                 ans+=1ll*a.back()*cb;
                a.pop_back();
                ca++;
            }
            else{
                ans+=1ll*b.back()*ca;
                b.pop_back();
                cb++;
            }
        }
        return ans;
    }
};
