class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end());
        reverse(h.begin(),h.end());
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        int cnth=0;
        int cntv=0;
        int i=0;
        int j=0;
        long long ans=0;
        n-=1;
        m-=1;
        while(i<m and j<n){
            if(h[i]>v[j]){
                ans += (cntv+1)*h[i];
                cnth+=1;
                i+=1;
            }
            else{
                ans+=(cnth+1)*v[j];
                cntv+=1;
                j+=1;
            }
        }
        while(i<m){
            ans += (cntv+1)*h[i];
            cnth+=1;
            i+=1;
        }
        while(j<n){
            ans+=(cnth+1)*v[j];
            cntv+=1;
            j+=1;
        }
        return ans;
    }
};
