class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hCuts = sorted(horizontalCut)
        hCuts.reverse()
        vCuts = sorted(verticalCut)
        vCuts.reverse()
        hP,vP,i,j ,ans = 1,1,0,0,0
        while i < len(hCuts) and j < len(vCuts):
            if hCuts[i] >= vCuts[j]:
                ans += hCuts[i] * vP
                hP += 1
                i += 1
            else:
                ans += vCuts[j] * hP
                vP += 1
                j += 1
        while j < len(vCuts):
            ans += vCuts[j] * hP
            j += 1
        while i < len(hCuts):
            ans += hCuts[i] * vP
            i += 1
        return ans
