class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        typedef long long LL;
        LL ans = 0;
        LL rcnt = 1, ccnt = 1;
        sort(hc.begin(), hc.end(), greater<int>());
        sort(vc.begin(), vc.end(), greater<int>());
        for(int i = 0, j = 0; i < hc.size() || j < vc.size();) {
            if(i == hc.size()) ans += vc[j ++] * rcnt, ccnt ++;
            else if(j == vc.size()) ans += hc[i ++] * ccnt, rcnt ++;
            else if(hc[i] >= vc[j]) ans += hc[i ++] * ccnt, rcnt ++;
            else ans += vc[j ++] * rcnt, ccnt ++;
        }
        return ans;
    }
};
