class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        long long totalCost = 0;
        int hori_pie = 1, vert_pie = 1;
        int hori_ind = 0, vert_ind = 0;

        while (hori_ind < m - 1 || vert_ind < n - 1) {
            if (vert_ind == n - 1 || (hori_ind < m - 1 && horizontalCut[hori_ind] > verticalCut[vert_ind])) {
                totalCost += (long long)horizontalCut[hori_ind] * vert_pie;
                hori_pie++;
                hori_ind++;
            } else {
                totalCost += (long long)verticalCut[vert_ind] * hori_pie;
                vert_pie++;
                vert_ind++;
            }
        }

        return totalCost;
    }
};
