class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
    sort(hc.begin(), hc.end(), greater<int>());
    sort(vc.begin(), vc.end(), greater<int>());

    long long hi = 0, vi = 0;
    long long hp = 1, vp = 1;
    long long tc = 0;

    while (hi < hc.size() && vi < vc.size()) {
        if (hc[hi] >= vc[vi]) {
            tc += hc[hi] * vp;
            ++hi;
            ++hp;
        } else {
            tc += vc[vi] * hp;
            ++vi;
            ++vp;
        }
    }

    while (hi < hc.size()) {
        tc += hc[hi] * vp;
        ++hi;
        ++hp;
    }

    while (vi < vc.size()) {
        tc += vc[vi] * hp;
        ++vi;
        ++vp;
    }

    return tc;
    }
};
