class Solution {
public:
    #define ll long long
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<pair<int,int>> v;
        for(int i=0;i+1<m;i++){
            v.push_back({hc[i],0});
        }
        for(int i=0;i+1<n;i++){
            v.push_back({vc[i],1});
        }
        priority_queue<pair<int,int>>pq;

        for(auto i:v){
            pq.push(i);
        }
        int hcom = 1;
        int vcom = 1;
        ll ans = 0;
        while(!pq.empty()){
            pair<int,int>p = pq.top();
            pq.pop();
            int id = p.second;
            if(id == 0){
                // hc cut
                ans += (ll)vcom*(ll)p.first;
                hcom++;
            }
            else{
                ans += (ll)hcom*(ll)p.first;
                vcom++;
                // vc cut
            }
        }
        return ans;
        return 0;
    }
};
