typedef long long ll;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<array<ll, 2>>a;
        for (int i = 0; i < h.size(); i++) {
            a.push_back({h[i],0});
        }
        for (int i = 0; i < v.size(); i++) {
            a.push_back({v[i],1});
        }
        sort(a.begin(), a.end());
        reverse(a.begin(), a.end());
        vector<ll>mult(2,1);
        ll ans = 0;
        for (auto [amt, t] : a) {
            ans += amt * mult[t];
            mult[t^1]++;
        }
        return ans;
    }
};
