#include <bits/stdc++.h>

using namespace std;

using LL = long long;

class BIT {
    int n;
    vector<int> tr;
public:
    BIT(int _n) {
        n = _n;
        tr.resize(n + 1);
    }

    void update(int x) {
        while (x <= n) {
            tr[x]++;
            x += x & -x;
        }
    }

    int query(int x) {
        int res = 0;
        while (x) {
            res += tr[x];
            x -= x & -x;
        }
        return res;
    }
};

class Solution {
public:
    long long minimumCost(int m, int n, vector<int> &horizontalCut, vector<int> &verticalCut) {
        vector<pair<int, int>> v;
        for (int i = 0; i < m - 1; i++) {
            v.emplace_back(horizontalCut[i], -1);
        }
        for (int i = 0; i < n - 1; i++) {
            v.emplace_back(verticalCut[i], 1);
        }
        sort(v.begin(), v.end(), greater<>());
        long long res = 0;
        LL ho = 1, ver = 1;
        for (auto &p: v) {
            int line = p.first, type = p.second;
            if (type == -1) {
                res += ver * line;
                ho++;
            } else {
                res += ho * line;
                ver++;
            }
        }
        return res;
    }
};
