class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        multiset<pair<int,char>>sett;
        long long h = 0;
        long long v = 0;
        for(auto &it : horizontalCut){
            sett.insert({it,'H'});
        }
        for(auto &it : verticalCut){
            sett.insert({it,'V'});
        }
        long long ans = 0;

        while(!sett.empty()){
            auto last = *(--(sett.end()));
            int val = last.first;
            char type = last.second;
            sett.erase(--sett.end());
            if(type=='H'){
                ans+=((v+1)*(val));
                h++;
            }
            else{
                ans+=((h+1)*(val));
                v++;
            }
        }
        return ans;
    }
};
