class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long,bool>>v ;
        for(auto i:horizontalCut){
            v.push_back({i,0}) ;
        }  
        for(auto i:verticalCut){
            v.push_back({i,1}) ;
        } 
        sort(v.begin(),v.end(),greater<>()) ;
        long long hor=0 ;
        long long ver=0 ;
        long long ans=0 ;
        for(auto i:v){
            if(i.second==0){
                ans = ans+(1LL)*(ver+1)*i.first ;
                ++hor ;
            }
            else{
                ans = ans+(1LL)*(hor+1)*i.first ;
                ++ver;
            }
        }
        return ans ;    
    }
};
