class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
      vector<vector<int>> o;
      for(int i = 0; i<m-1; ++i) {
        o.push_back(vector<int>{-h[i], 0});
      }
      for(int i = 0; i<n-1; ++i) {
        o.push_back(vector<int>{-v[i], 1});
      }
      sort(o.begin(), o.end());
      vector<int> cnt ={1, 1}; long long ans = 0;
      for(vector<int> &cut:o ){
        int cost = -cut[0], i = cut[1];
        ans += 1ll * cost * cnt[1-i];
        cnt[i]++;
      }
      return ans;
    }
};
