class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = [(cost, 0) for cost in horizontalCut] + [(cost, 1) for cost in verticalCut]
        cuts.sort(reverse=True, key=lambda x: x[0])
        horizontal_segments = 1
        vertical_segments = 1
        total_cost = 0
        for cost, cut_type in cuts:
            if cut_type == 0:  
                total_cost += cost * vertical_segments
                horizontal_segments += 1
            else: 
                total_cost += cost * horizontal_segments
                vertical_segments += 1

        return total_cost
