class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) 
    {
        int h=1;
        int v=1;
        long long ans=0;
        m--;
        n--;
        priority_queue<pair<int,int>> pq;
        for(int i=0;i<m;i++)
        {
            pq.push(make_pair(hc[i],1));
        }
        for(int i=0;i<n;i++)
        {
            pq.push(make_pair(vc[i],2));
        }
        while(!pq.empty())
        {
            int d=pq.top().second;
            int c=pq.top().first;
            pq.pop();
            if(d==1)
            {
                ans+=v*c;
                h++;
            }
            else
            {
                ans+=h*c;
                v++;
            }
        }
        return ans;
    }
};
