class Solution {
public:
    struct Node {
        int w;
        bool r;
        bool operator<(const Node &rhs) const {
            return w > rhs.w;
        }
    };
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<Node> q;

        for (auto h : horizontalCut) {
            q.push(Node{h, true});    
        }
        for (auto v : verticalCut) {
            q.push(Node{v, false});
        }
        long long ans = 0;
        while (!q.empty()) {
            Node p = q.top(); q.pop();
            if (p.r) {
                ans += 1LL* p.w * n;
                m--;
            } else {
                ans += 1LL*p.w * m;
                n--;
            }
        }
        return ans;
    }
};
