class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        res = 0

        horizontalCut.sort()
        h_cuts = horizontalCut
        verticalCut.sort()
        v_cuts = verticalCut

        h = 0
        v = 0

        while h_cuts and v_cuts:
            if h_cuts[-1] > v_cuts[-1]:
                res += (v + 1) * h_cuts.pop()
                h += 1
            else:
                res += (h + 1) * v_cuts.pop()
                v += 1

        while h_cuts:
            res += (v + 1) * h_cuts.pop()

        while v_cuts:
            res += (h + 1) * v_cuts.pop()

        return res
