class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        res = 0
        dp = [1, 1]
        for ii, jj in sorted([(i, 0) for i in horizontalCut] + [(i, 1) for i in verticalCut], key=lambda i:(-i[0])):
            res += ii * dp[jj]
            # print(ii, dp[jj])
            dp[(jj + 1) % 2] += 1
        return res
