class Solution:
    def minimumCost(self, m: int, n: int, hC: List[int], vC: List[int]) -> int:
        hC = [-it for it in hC]
        vC = [-it for it in vC]
        heapq.heapify(hC)
        heapq.heapify(vC)
        ct1=1
        ct2=1
        ans=0
        while hC and vC:
            if hC[0]<=vC[0]:
                ans+=ct2*heapq.heappop(hC)
                ct1+=1
            else:
                ans+=ct1*heapq.heappop(vC)
                ct2+=1
        while hC:
            ans+=ct2*heapq.heappop(hC)
        while vC:
            ans+=ct1*heappop(vC)
        return -ans
