class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        // return solve(0,0,hor,ver);
        long long ans=0;
        sort(hor.begin(),hor.end());
        sort(ver.begin(),ver.end());

        int i=m-2,j=n-2;
        long long v=1,h=1;
        while(i>=0 && j>=0) {
            if(hor[i]>ver[j]) {
                ans += hor[i]*v;
                h++;
                i--;
            }
            else {
                ans += ver[j]*h;
                v++;
                j--;
            }
        }

        while(i>=0) {
            ans += hor[i--]*v;
                h++;
        }

        while(j>=0) {
            ans += ver[j--]*h;
                v++;
        }

        return ans;
    }

};
