#define ll long long

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<array<int, 3>> a;
        for(int i = 0; i < m-1; i++){ 
            a.push_back({hc[i], i, 0});
        }
        for(int i = 0; i < n-1; i++){ 
            a.push_back({vc[i], i, 1});
        }

        sort(a.rbegin(), a.rend());
        ll res = 0;
        vector<ll> cnt(2, 1);
        for(int i = 0; i < m-1+n-1; i++){
            if(a[i][2]){
                res += cnt[0] * a[i][0];
                cnt[1]++;
            }
            else{
                res += cnt[1] * a[i][0];
                cnt[0]++;
            }
        }
        return res;
    }
};
