class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        c = [(h, 0) for h in horizontalCut] + [(h, 1) for h in verticalCut]
        rows, cols = 0, 0
        c.sort(reverse = True)
        res = 0
        for item, key in c:
            if key == 0:
                res += item * (cols + 1)
                rows += 1
            else:
                res += item * (rows + 1)
                cols += 1
        return res
