class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
         sort(hCut.begin(),hCut.end());
        sort(vCut.begin(),vCut.end());
        priority_queue<pair<long long int,long long int>,vector<pair<long long int,long long int>>>pq;
        for(auto c:hCut)
        {
        pq.push({c,1});
        }
         for(auto c:vCut)
        {
        pq.push({c,0});
        }
       long long int ans=0;
        long long int valh=1;
        long int valv=1;
        while(!pq.empty())
        {
            auto p=pq.top();
            pq.pop();
            if(p.second==1)
            {
                ans+=p.first*valh;
                valv++;
            }
            else
            {
                 ans+=p.first*valv;
                valh++;

            }
        }
        return ans;

    }
};
