#define ll long long
class Solution {
public:
    ll minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<ll,ll>> a;
        for(ll i=0;i<m-1;i++){
            a.push_back({h[i],0});
        }
        for(ll i=0;i<n-1;i++){
            a.push_back({v[i],1});
        }
        sort(a.begin(),a.end(),greater<>());
        ll ans=0,hc=1,vc=1;
        for(ll i=0;i<a.size();i++){
            if(a[i].second==0){
                ans+= (vc*a[i].first);
                hc++;
            }
            else{
                ans+= (hc*a[i].first);
                vc++;
            }
        }
        return ans;
    }
};
