class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ml, nl = len(horizontalCut), len(verticalCut)
        cuts = []
        for i, x in enumerate(horizontalCut):
            cuts.append((x, i, 1))
        for j, x in enumerate(verticalCut):
            cuts.append((x, j, 0))
        cuts.sort(reverse = True)
        h = v = 1
        ans = 0
        for cost, i, is_hor in cuts:
            if is_hor:
                ans += v * cost
                h += 1
            else:
                ans += h * cost
                v += 1
        return ans
