class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, char>> cuts;
        for (int i : horizontalCut) {
            cuts.push_back({i, 'H'});
        }
        for (int i : verticalCut) {
            cuts.push_back({i, 'V'});
        }

        sort(cuts.rbegin(), cuts.rend());

        int h_segments = 1, v_segments = 1;
        long long totalCost = 0;

        for (auto cut : cuts) {
            if (cut.second == 'H') {
                totalCost += cut.first * v_segments;
                h_segments++;
            } else {
                totalCost += cut.first * h_segments;
                v_segments++;
            }
        }

        return totalCost;
    }
};
