class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        result = 0
        all_cuts = [(cut, 'H') for cut in horizontalCut] + [(cut, 'V') for cut in verticalCut]
        v_count = h_count = 0
        for cut, vh in sorted(all_cuts, reverse=True):
            if vh == 'H':
                h_count += 1
                result += cut * (v_count + 1)
            else:
                v_count += 1
                result += cut * (h_count + 1)
        return result
