class Solution 
{
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) 
    {
        vector<pair<int, bool>> overhead;//真代表行，假代表列
        for(auto num : horizontalCut)
            overhead.push_back({num, true});
        for(auto num : verticalCut)
            overhead.push_back({num, false});
        sort(overhead.begin(), overhead.end(), [](const auto &p1, const auto &p2) -> bool{
            return p1.first > p2.first;
        });
        long long row = 1, col = 1;
        long long ret = 0;
        for(auto [cost, flag] : overhead)
        {
            if(flag)
            {
                row++;
                ret += (col * cost);
            }
            else
            {
                col++;
                ret += (row * cost);
            }
        } 
        return ret;
    }
};
