class Solution {
public:
    #define PR(output) cout << output << endl
#define IN(input) cin >> input

// Function implementations
void kaatneKeLiyeTaiyaarKaro(vector<int>& katneKeKharach) {
    sort(katneKeKharach.begin(), katneKeKharach.end(), greater<int>());
}

priority_queue<pair<int, char>> sabseZyadaMahengaKaatneKaSamuh(vector<int>& aaksheRambha, vector<int>& vaaShaw) {
    priority_queue<pair<int, char>> pq;
    for (int kharach : aaksheRambha) {
        pq.push({kharach, 'H'});
    }
    for (int kharach : vaaShaw) {
        pq.push({kharach, 'V'});
    }
    return pq;
}

long long visheshKharach(int kharach, int tukde) {
    return static_cast<long long>(kharach) * tukde;
}

void sabseMahengaKaat(priority_queue<pair<int, char>>& pq, long long& kulKharach, int& horizontalTukde, int& verticalTukde) {
    auto [kharach, prakar] = pq.top();
    pq.pop();

    if (prakar == 'H') {
        kulKharach += visheshKharach(kharach, verticalTukde);
        horizontalTukde++;
    } else {
        kulKharach += visheshKharach(kharach, horizontalTukde);
        verticalTukde++;
    }
}

long long sampoornKharach(vector<int>& aaksheRambha, vector<int>& vaaShaw) {
    kaatneKeLiyeTaiyaarKaro(aaksheRambha);
    kaatneKeLiyeTaiyaarKaro(vaaShaw);

    priority_queue<pair<int, char>> pq = sabseZyadaMahengaKaatneKaSamuh(aaksheRambha, vaaShaw);

    long long kulKharach = 0;
    int horizontalTukde = 1, verticalTukde = 1;

    while (!pq.empty()) {
        sabseMahengaKaat(pq, kulKharach, horizontalTukde, verticalTukde);
    }

    return kulKharach;
}

long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
    return sampoornKharach(horizontalCut, verticalCut);
}
};
