class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>>cuts;
        for (auto h:horizontalCut) {
            cuts.push_back({h, 0});
        }
        for (auto v:verticalCut) {
            cuts.push_back({v, 1});
        }

        sort (cuts.rbegin(), cuts.rend());

        long long sum = 0;
        int x = 1, y = 1;
        for (auto [cost, type]:cuts) {
            if (type == 0) { // horizontal
                sum += 1ll * y * cost;
                x += 1;
            } else { // vertical
                sum += 1ll * x * cost;
                y += 1;
            }
        }
        return sum;

    }
};
