class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long cost=0;
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        int i=0,j=0;
        int horicuts=0,vertcuts=0;
        while(i<h.size() && j<v.size()){
            if(h[i]>=v[j]){
                cost+=h[i]*(vertcuts+1);
                horicuts++;
                i++;
            }
            else{
                cost+=v[j]*(horicuts+1);
                vertcuts++;
                j++;
            }
        }
        while(i<h.size()){
            cost+=h[i]*(vertcuts+1);
                horicuts++;
                i++;
        }
        while(j<v.size()){
            cost+=v[j]*(horicuts+1);
                vertcuts++;
                j++;
        }
        return cost;
    }
};
