class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());
        int i = 0, j = 0;
        int ch = 1, cv = 1;
        long long ans = 0;
        while(i<m-1 && j<n-1){
            if(h[i] > v[j]){
                ans += 1LL*h[i]*ch;
                i++;
                cv++;
            }else if(h[i] < v[j]){
                ans += 1LL*v[j]*cv;
                j++;
                ch++;
            }else if(h[i] == v[j]){
                if(ch>=cv){
                    ans += 1LL*h[i]*ch;
                    i++;
                    cv++;
                }else{
                    ans += 1LL*v[j]*cv;
                    j++;
                    ch++;
                }
            }
        }
        while(i<m-1){
            ans += 1LL*h[i]*ch;
            i++;
            cv++;
        }
        while(j<n-1){
            ans += 1LL*v[j]*cv;
            j++;
            ch++;
        }
        return ans;
    }
};
