class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hC, vector<int>& vC) {

        long long m_piece = 1, n_piece = 1, ans = 0;
        sort(hC.rbegin(), hC.rend());
        sort(vC.rbegin(), vC.rend());
        int i = 0, j = 0;
        while(i < hC.size() && j < vC.size()){
            if(hC[i] > vC[j]){
                ans += hC[i] * n_piece;
                m_piece += 1;
                i++;
            }   
            else{
                ans += vC[j] * m_piece;
                n_piece += 1;
                j++;
            }
        }
        while(i < hC.size()){
            ans += hC[i] * n_piece;
            i++; 
        }
        while(j < vC.size()){
            ans += vC[j] * m_piece;
            j++;
        }
        return ans;
    }
};
