class Solution {
public:
    #define ll long long
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<ll,char>>v;
        for(auto it:horizontalCut){
         v.push_back({it,'H'});
       }
        for(auto it:verticalCut){
         v.push_back({it,'V'});
        }

    sort(v.begin(),v.end(),greater<pair<ll,char>>());
    ll hCnt=1;
    ll vCnt=1;
    ll ans=0;
    for(auto it:v){
        if(it.second=='H'){
            ans+=it.first*vCnt;
            hCnt++;
        } 
        else{
            ans+=it.first*hCnt;
              vCnt++;
        }
    }
    return ans;
    }
};
