class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = []

        for cost in horizontalCut:
            cuts.append((cost, True))
        for cost in verticalCut:
            cuts.append((cost, False))

        # Sort the cuts in descending order based on cost
        cuts.sort(reverse=True, key=lambda x: x[0])

        total_cost = 0
        horizontal_pieces = 1
        vertical_pieces = 1

        for cost, is_horizontal in cuts:
            if is_horizontal:
                total_cost += cost * vertical_pieces
                horizontal_pieces += 1
            else:
                total_cost += cost * horizontal_pieces
                vertical_pieces += 1

        return total_cost
